package com.syntax.class08;

public class NestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int x=1; x<=3; x++) {
			//System.out.println(x); burda degil de asagida print edebiliyoruz
			
			for(int y=1; y<=3; y++) {
				System.out.println(x+" "+y);
				//break;
				
			}
			
			
			
		}

	}

}
