package com.syntax.class09;

public class ForLoopExr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 6; i++) {
			for (int j = 1; j <= i; j++) {
				System.out.print(j);
				}System.out.println();
				}
	}
}
