package com.syntax.class09;

public class OdometerForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int x=0; x<=9; x++) {
			for(int y=0; y<=9; y++) {
				for(int z=0; z<=9; z++) {
					for(int t=0; t<=9; t++) {
						System.out.println(x+""+y+""+z+""+t);
					}
				}
			}
		}
	}

}
