package com.syntax.class09;

public class Repl69 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
		for(int x=0; x<=5; x++) {
			for(int y=1; y<=7-x; y++) {
				System.out.print(y+" "); 
			}
			System.out.println();
		}
		
		for(int x=1; x<=7; x++) {
			for(int y=1; y<=x; y++) {
				System.out.print(y+" "); 
			}
			System.out.println();
		}
		
	//bunda bosluklari kapatmayinca repl kabul etmedi-repl da hata var	
	}

}
