package com.syntax.class09;

public class Repl80 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double[][] a = {
				{1.4,2.0,3.3,2},
				{4,1.5,6.1,1},
				{1.2,3.1,4,1.6}
			};
			
			//Double and print array
		    for(int x=0; x<a.length; x++){
			  for(int y=0; y<a[x].length; y++){
			    System.out.print(a[x][y]+" ");
			  }
			   System.out.println();
	}
	}
}
