package com.syntax.class10;

public class Cimbom {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

String c="cimbom oley mayislar bizimdir";
		
		System.out.println(c.contains("fener"));
		System.out.println(c.contains("cimbom"));
		System.out.println(c.contains("bom"));
		System.out.println(c.contains("zim"));
		System.out.println("....................................");
		System.out.println(c.startsWith("cimbom"));
		System.out.println(c.endsWith("bizimdir"));
	}

}
