package yeniOgrendiklerimSelf;

public class MethodDneme {

	public static int sign(int x) {
		if(x>0) {
			return 1;
		}else if(x==0) {
			return 0;
		}else {
			return -1;
		}
	}
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(sign(-143));;
	}

}
